function scrollTop() {
  window.scrollTo({ top: 0, behavior: "smooth" });
}

var body = document.querySelector("body");

function openMenu() {
  var element = document.querySelectorAll(".header-menu-itens .menu-items");
  element.forEach(function (el) {
    el.classList.add("flex");
  });
  scrollTop();
  body.classList.add("overflow-hidden");
}

function closeMenu() {
  var close = document.querySelectorAll(".header-menu-itens .menu-items");
  close.forEach(function (item) {
    item.classList.remove("flex");
  });
  body.classList.remove("overflow-hidden");
}


document.addEventListener('DOMContentLoaded', function () {
  const items = document.querySelectorAll('.accordion-item');

  items.forEach(item => {
    const header = item.querySelector('.accordion-header');
    const body = item.querySelector('.accordion-body');

    header.addEventListener('click', () => {
      // Fechar todos os itens
      items.forEach(i => {
        if (i !== item) {
          i.querySelector('.accordion-body').style.display = 'none';
          i.querySelector('.accordion-item').classList.remove('active');
        }
      });

      items.classList.add('active');

      // Alternar o estado do item clicado
      body.style.display = body.style.display === 'block' ? 'none' : 'block';
    });
  });
});
