const swiperElement = document.querySelector(".main-banner .swiper-container");
const swiperWrapper = swiperElement.querySelector(".swiper-wrapper");

const swiperSlides = swiperElement.querySelectorAll(".swiper-slide");
swiperSlides.forEach((slide,_) => {
  const clonedSlide = slide.cloneNode(true);
  swiperWrapper.appendChild(clonedSlide);
});

const swiper = new Swiper(swiperElement, {
  slidesPerView: 1,
  spaceBetween: 0,
  autoplay: {
    delay: 7000,
  },
  loop: false,
  navigation: {
    nextEl: ".swiper-button-next",
    prevEl: ".swiper-button-prev"
  },
});

const swiperDepositions = document.querySelector(".depositions .swiper-container");
const swiperWrapperDepositions = swiperDepositions.querySelector(".depositions .swiper-wrapper");

const swiperSlidesDepositions = swiperDepositions.querySelectorAll(".swiper-slide");
swiperSlidesDepositions.forEach((slide,_) => {
  const clonedSlide = slide.cloneNode(true);
  swiperWrapperDepositions.appendChild(clonedSlide);
});

const swiperDepo = new Swiper(swiperDepositions, {
  slidesPerView: 2,
  spaceBetween: 20,
  autoplay: {
    delay: 7000,
  },
  loop: false,
  navigation: {
    nextEl: ".swiper-button-next",
    prevEl: ".swiper-button-prev"
  },
  breakpoints: {
    375: {
      slidesPerView: 1,
    },
    1280: {
      slidesPerView: 2,
    },
  }
});