const swiperElement = document.querySelector(".main-banner .swiper-container");
const swiperWrapper = swiperElement.querySelector(".swiper-wrapper");

const swiperSlides = swiperElement.querySelectorAll(".swiper-slide");
swiperSlides.forEach((slide,_) => {
  const clonedSlide = slide.cloneNode(true);
  swiperWrapper.appendChild(clonedSlide);
});

const swiper = new Swiper(swiperElement, {
  slidesPerView: 1,
  spaceBetween: 0,
  autoplay: {
    delay: 7000,
  },
  loop: false,
  navigation: {
    nextEl: ".swiper-button-next",
    prevEl: ".swiper-button-prev"
  },
});

const swiperElementService = document.querySelector(".service-image .swiper-container");
const swiperService = new Swiper(swiperElementService, {
  autoplay: {
    delay: 7000,
  },
  loop: true,
  navigation: {
    nextEl: ".swiper-button-next",
    prevEl: ".swiper-button-prev"
  },
  breakpoints: {
    375: {
      slidesPerView: 1,
      grid: {
        rows: 2,
      },
      spaceBetween: 10,
    },

    768: {
      slidesPerView: 2,
      grid: {
        rows: 2,
      },
      spaceBetween: 35,
    },

    1024: {
      slidesPerView: 2,
      grid: {
        rows: 2,
      },
      spaceBetween: 20,
    },
    
    1536: {
      slidesPerView: 3,
      spaceBetween: 40,
      grid: {
        rows: 2,
      },
    },
  },
});

